const signal = "";

const a = "a".charCodeAt(0);
let len = 14;
let win = 0;
let mark = 0;
let bin = new Array(26).fill(0);
let dup = 0;

function setup() {
    // frameRate(10);
    createCanvas(800, 400);
}

function draw() {
    background(51);

    textSize(24);
    textAlign(LEFT, TOP);
    noStroke();
    fill(0, 255, 0);
    text("Start-of-message = " + mark, 15, 15);

    strokeWeight(1);
    stroke(0, 153, 0);
    noFill();
    for (let i = 0; i < win; ++i) {
        rect(11 + (6 + i) * 30, 355, 28, 28);
    }

    textSize(28);
    textAlign(CENTER, CENTER);
    for (let i = 0; i < 26; ++i) {
        strokeWeight(1);
        stroke(0, 102, 0);
        fill(0, 153, 0);
        text(String.fromCharCode(a + i), 25 + i * 30, 320);

        strokeWeight(3);
        stroke(0, 153, 0);
        if (bin[i] > 0) {
            fill(0, 204, 0);
            for (let j = 0; j < bin[i]; ++j) {
                rect(15 + i * 30, 280 - j * 30, 20, 20);
            }
        } else {
            noFill();
            rect(15 + i * 30, 280, 20, 20);
        }
    }

    if (win < len) {
        const c = signal.charCodeAt(mark) - a;
        if (++bin[c] == 2) {
            ++dup;
        }
        ++mark;
        ++win;
    } else if (dup > 0) {
        let c = signal.charCodeAt(mark) - a;
        if (++bin[c] == 2) {
            ++dup;
        }
        c = signal.charCodeAt(mark - len) - a;
        if (--bin[c] == 1) {
            --dup;
        }
        ++mark;
    } else {
        noLoop();
    }
}
